parameters.query:
={{ (function() {
  const query = $fromAI('sql_query', '√áalƒ±≈ütƒ±rƒ±lacak SQL sorgusu');

  // G√ºvenlik kontrol√º - sadece SELECT sorgularƒ±na izin ver
  if (!query.trim().toUpperCase().startsWith('SELECT')) {
    throw new Error('Sadece SELECT sorgularƒ± √ßalƒ±≈ütƒ±rƒ±labilir');
  }

  // Tehlikeli komutlarƒ± kontrol et
  const dangerousKeywords = ['DROP', 'DELETE', 'UPDATE', 'INSERT', 'CREATE', 'ALTER', 'TRUNCATE'];
  const upperQuery = query.toUpperCase();

  for (const keyword of dangerousKeywords) {
    if (upperQuery.includes(keyword)) {
      throw new Error(`G√ºvenlik: ${keyword} komutu kullanƒ±lamaz`);
    }
  }

  // LIMIT kontrol√º
  if (!upperQuery.includes('LIMIT')) {
    return query + ' LIMIT 50';
  }

  return query;
})() }}

